import raf from '../../../_util/raf';
import { onBeforeUnmount, shallowRef } from 'vue';
/**
 * Always trigger latest once when call multiple time
 */
export default (() => {
  const idRef = shallowRef(0);
  const cleanUp = () => {
    raf.cancel(idRef.value);
  };
  onBeforeUnmount(() => {
    cleanUp();
  });
  return callback => {
    cleanUp();
    idRef.value = raf(() => {
      callback();
    });
  };
});
